        public Day01()
        {
            var result = Stage2();

            Console.WriteLine(result);

            Clipboard.SetText(result);
        }

        private string Stage1()
        {
            var up = input.Count(d => d == '(');
            var down = input.Count(d => d == ')');

            var result = up - down;

            return result.ToString();
        }

        private string Stage2()
        {
            int count = 0;

            for (int i = 0; i < input.Length; i++)
            {
                if (input[i] == '(')
                {
                    count++;
                }

                if (input[i] == ')')
                {
                    count--;
                }

                if (count < 0)
                    return (i + 1).ToString();
            }

            return String.Empty;
        }
